mapper.select:
        SELECT *
        FROM 


				AND serviceName = #{serviceName}
				AND serviceNo = #{serviceNo}


			AND fileNo = #{fileNo}

       	ORDER BY
       		fileNo ASC

        SELECT *
        FROM 

			serviceName = #{serviceName}
			AND serviceNo = #{serviceNo}

       	ORDER BY
       		fileNo ASC

SELECT count(*)
FROM 



			AND serviceName = #{serviceName}
			AND serviceNo = #{serviceNo}


			AND fileNo = #{fileNo}



ORDER BY
	fileNo ASC

mapper.delete:
        DELETE FROM 


				AND serviceName = #{serviceName}
				AND serviceNo = #{serviceNo}


			AND fileNo = #{fileNo}

        DELETE FROM 



					AND serviceName = #{serviceName}
					AND serviceNo = #{serviceNo}


					AND fileNo IN

						#{fileNo}




						AND serviceName = #{serviceName}
